var bamboolib = require('./index');
var code_export = require('./code_export.js');

var base = require('@jupyter-widgets/base');
var notebook = require('@jupyterlab/notebook');

/**
 * The widget manager provider.
 */
module.exports = {
  id: 'bamboolib',
  requires: [base.IJupyterWidgetRegistry, notebook.INotebookTracker,],
  activate: function(app, widgets, notebookTracker) {
      widgets.registerWidget({
          name: 'bamboolib',
          version: bamboolib.version,
          exports: bamboolib
      });

      code_export.CodeExporter.setupJupyterLab(notebookTracker)
    },
  autoStart: true
};
